INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextid`, `TextRange`, `comment`) VALUES 
(37198, 5, 0, 'There\'s plenty else going on out here, too. We\'ve opened up the path to the airport, there\'s rumors of Dark Irons in the east... oh, and I think something weird is going on with that dwarf king, Magni. Might want to check it out.', 12, 0, 100, 0, 0, 0, 0, 0, 'Milo\'s Gyro'),
(37198, 4, 0, 'And if you\'ll look to your left, you\'ll see that the gnomes have retaken Gnomeregan. Hooray for us!', 12, 0, 100, 0, 0, 0, 0, 0, 'Milo\'s Gyro'),
(37198, 3, 0, 'If you thought you had problems with trolls in Coldridge, wait \'til you get to Kharanos! That big cataclysm got the trolls are stirred up out here.', 12, 0, 100, 0, 0, 0, 0, 0, 'Milo\'s Gyro'),
(37198, 1, 0, 'Ahh, there you are. Hello! I figured that, while you\'re flying to Kharanos, I\'d give you some data about what\'s going on outside of Coldridge Valley.', 12, 0, 100, 0, 0, 0, 0, 0, 'Milo\'s Gyro'),
(37198, 2, 0, 'It seems you Coldridge folks are dreadfully under-informed about the events of the last few days.', 12, 0, 100, 0, 0, 0, 0, 0, 'Milo\'s Gyro'),
(37198, 0, 0, '<Static> Is this thing on? Hello?', 12, 0, 100, 0, 0, 0, 0, 0, 'Milo\'s Gyro'),
(37198, 6, 0, 'And here we are! Welcome to Kharanos! Watch your step on the way out.', 12, 0, 100, 0, 0, 0, 0, 0, 'Milo\'s Gyro');
